mapper.insert:
/* Qna - qnaInsert */
select q_num_seq.nextval from dual
INSERT INTO qna(q_num, c_nickname, q_content, q_title, q_category, q_imgurl,q_writedate, q_repindent,q_reproot,q_repstep, c_num) VALUES (#{q_num}, #{c_nickname}, #{q_content}, #{q_title}, #{q_category}, #{q_imgurl}, sysdate, 0, q_num_seq.currval, 0, #{c_num})

mapper.update:
/*Qna - QnaReadCount */
UPDATE qna
SET
	q_readcnt = q_readcnt+1
WHERE q_num = #{q_num}

/* Qna - qnaUpdate */
UPDATE qna
		SET q_title = #{q_title}
		  , q_content = #{q_content}
		  , q_writedate = sysdate
WHERE q_num = #{q_num}

UPDATE qna
		SET q_repStep = q_repStep + 1
		WHERE q_repRoot = #{q_repRoot}
		AND q_repStep > #{q_repStep}
